mapper.select:
SELECT COUNT(*)
FROM member
WHERE mem_id=#{mem_id} AND password=#{password}

SELECT COUNT(*) 
FROM mobileinfo
WHERE phonenumber=#{phone}

SELECT NVL(islogin,0)
FROM mobileinfo
WHERE phonenumber=#{phone}

