SELECT airportNo
FROM airport
WHERE airportName=#{location}

SELECT airportname
FROM airport
WHERE airportNo=#{routeNo}

update.if:
UPDATE airplane
SET nomal = nomal - #{memSize}
WHERE airPlaneNo = #{airPlaneNo}

UPDATE airplane
SET highClass = highClass - #{memSize}
WHERE airPlaneNo = #{airPlaneNo}

UPDATE airplane
SET premium = premium - #{memSize}
WHERE airPlaneNo = #{airPlaneNo}

