mapper.update:
UPDATE airplane
SET premium = premium+#{premium}, highClass = highClass+#{highClass}, nomal = nomal+#{nomal} 
WHERE airPlaneNo=#{airPlaneNo}

mapper.select:
SELECT rentcar.use_km,rentcar.car_count,rentcar.insurance,rentcar.car_num,rentcar.car_img, car.car_name, car.car_kind, car.fuel, car.brand_name, car.car_price,car.car_addprice,rentcar.rent_no FROM car JOIN rentcar ON rentcar.car_num=car.car_num WHERE rent_no not in( SELECT rent_no FROM reservation_car WHERE to_timestamp(#{rental_day}||' '||#{rental_time}) BETWEEN to_timestamp(rental_day||' '||rental_time) AND to_timestamp(return_day||' '||return_time) )
AND car_kind=#{car_kind}
ORDER BY car.car_price
