mapper.select:

	SELECT * FROM(SELECT planNo, mem_id, planTitle, PlanContent, rownum rNum
	from(select * from plan order by planNo desc))
	where rNum >= #{start} and rNum <= #{end}


	SELECT * FROM(SELECT planNo, mem_id, planTitle, PlanContent, rownum rNum
	from(select * from plan order by planNo desc))
	where rNum >= #{start} and rNum <= #{end}
	and mem_id = #{mem_id}
